  "AMSC661": {
    "id": "AMSC661",
    "name": "Scientific Computing II",
    "credits": "3",
    "description": "Numerical methods for solving ordinary and partial differential equations (elliptic, parabolic, hyperbolic, and dispersive): motivation, analysis, and implementation. Finite difference methods, finite element methods, Fourier and Chebyshev spectral methods, and meshless methods."
  },
  "AMSC663": {
    "id": "AMSC663",
    "name": "Advanced Scientific Computing I",
    "credits": "3",
    "description": "In the sequence Advanced Scientific Computing I & Advanced Scientific Computing II, (CMSC663/CMSC663 and AMSC664/CMSC664, respectively) students work on a year-long individual project to develop software for a scientific task in a high performance computing environment. Lectures will be given on available computational environments, code development, implementation of parallel algorithms."
  },
  "AMSC674": {
    "id": "AMSC674",
    "name": "Partial Differential Equations II",
    "credits": "3",
    "description": "Boundary value problems for elliptic partial differential equations via operator-theoretic methods. Hilbert spaces of functions. Duality, weak convergence. Sobolev spaces. Spectral theory of compact operators. Eigenfunction expansions."
  },
